import React from "react";
import TileCard from '../TileCard/TileCard'


const tiles = [
    {
        id: 1,
        title: '2023 Tesla Model S',
        description: 'Experience the ultimate in electric performance with the Tesla Model S. Featuring cutting-edge technology, unmatched range, and sleek design.',
        image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRL3tawp8KuAuodSiRqa5zWLoos75XIc_y1-L0QU0nNZD-dfQEUUS0MQByyHuPCO18VTECm&s",
    },
    {
        id: 2,
        title: '2023 Tesla Model S',
        description: 'Experience the ultimate in electric performance with the Tesla Model S. Featuring cutting-edge technology, unmatched range, and sleek design.',
        image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ9-DFYbYQpLthcGpxbgo8Jb_8057Gf7sGqjS9snRAZepylKDeXUubQU5Y&s",
    },
    {
        id: 3,
        title: "Tesla Model S",
        description: "A fully electric sedan with autopilot, fast acceleration, and a luxurious interior.",
        image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcST-oeQCpBAvrAcPlg0_pNTch7ufRQR8rYpYggL5_OAiGnFb-2Biq0bFLM&s",
    },
    // Додайте ще 3 елементи для демонстрації
    {
        id: 4,
        title: '2023 Tesla Model X',
        description: 'An SUV designed for family with all-wheel drive and autopilot features.',
        image: "https://example.com/image4.jpg",
    },
    {
        id: 5,
        title: '2023 Tesla Model Y',
        description: 'A versatile electric car with a spacious interior and cutting-edge technology.',
        image: "https://example.com/image5.jpg",
    },
    {
        id: 6,
        title: '2023 Tesla Roadster',
        description: 'An electric sports car with extreme speed and sleek design.',
        image: "https://example.com/image6.jpg",
    }
];


function TileCards () {
    const [visibleCount, setVisibleCount] = useState(3); // Початкова кількість видимих елементів

    const handleShowMore = () => {
        setVisibleCount((prevCount) => prevCount + 3); // Збільшуємо кількість видимих елементів на 3
    };

    return(
        <div>
            <div style={{ display: 'flex', justifyContent: 'space-between', flexWrap: 'wrap' }}>
                {tiles.slice(0, visibleCount).map(tile => (
                    <TileCard key={tile.id} title={tile.title} description={tile.description} image={tile.image} />
                ))}
            </div>
            {visibleCount < tiles.length && ( // Показуємо кнопку лише, якщо є ще елементи для показу
                <FooterButton onClick={handleShowMore} />
            )}
        </div>
    )
}

export default TileCards;
