// CarList.js
import React, { useState } from 'react';
import Car from '../Car/Car';
import { useNavigate } from 'react-router-dom';
import './carsList.css';
import Filters from '../Filters/Filters';
import Navigation from '../Navigation/Navigation'; // Імпортуємо компонент Navigation

const CarList = () => {
    const [filteredCars, setFilteredCars] = useState([]);
    const [searchQuery, setSearchQuery] = useState(''); // Додано стейт для пошукового запиту

    const [cars] = useState([
        { name: "Tesla Model S", description: "A fully electric sedan...", price: 79999, imageUrl: "..." },
        { name: "BMW M3", description: "A high-performance sports...", price: 69999, imageUrl: "..." },
        { name: "Audi R8", description: "A sleek and stylish sports...", price: 142000, imageUrl: "" },
        { name: "BMW M3", description: "A high-performance sports...", price: 69999, imageUrl: "..." },
        { name: "Audi R8", description: "A sleek and stylish sports...", price: 142000, imageUrl: "" },
    ]);

    const navigate = useNavigate();

    const handleShowMore = (car) => {
        navigate("/catalog/carPage", { state: { car } });
    };

    const handleSearch = (event) => {
        const query = event.target.value.toLowerCase();
        setSearchQuery(query);

        const filtered = cars.filter(car =>
            car.name.toLowerCase().includes(query) || car.description.toLowerCase().includes(query)
        );
        setFilteredCars(filtered);
    };

    const handleApplyFilters = (filters) => {
        const newFilteredCars = cars.filter(car => car.price <= filters.maxPrice);
        setFilteredCars(newFilteredCars);
    };

    const displayedCars = filteredCars.length > 0 ? filteredCars : cars;

    return (
        <div>
            <Navigation onSearch={handleSearch} /> {/* Додаємо компонент пошуку */}
            <Filters onClick={handleApplyFilters} />
            <div className="car-list">
                {displayedCars.map((car, index) => (
                    <Car key={index} {...car} onShowMore={() => handleShowMore(car)} />
                ))}
            </div>
        </div>
    );
};

export default CarList;
