import React, { useState } from 'react';
import './Cart.css';
import ActionButtons from '../../Catalog/CarPage/ActionButtons/ActionButtons';

const ShoppingCart = () => {
  const [items, setItems] = useState([
    { id: 1, name: 'Cropped Skinny Jean', quantity: 2, price: 45 },
    { id: 2, name: 'Cropped Skinny Jean', quantity: 1, price: 20 },
    { id: 3, name: 'Cropped Skinny Jean', quantity: 4, price: 160 }
  ]);

  const handleQuantityChange = (id, delta) => {
    setItems((prevItems) =>
      prevItems.map((item) =>
        item.id === id ? { ...item, quantity: Math.max(1, item.quantity + delta) } : item
      )
    );
  };

  const totalAmount = items.reduce((acc, item) => acc + item.quantity * item.price, 0);

  return (
    <div className="shopping-cart">
      <h2>Shopping Cart</h2>
      <div className="cart-items">
        {items.map((item) => (
          <div className="cart-item" key={item.id}>
            <div className="item-image"></div>
            <div className="item-info">
              <p>{item.name}</p>
            </div>
            <div className="item-quantity">
              <button onClick={() => handleQuantityChange(item.id, -1)}>-</button>
              <span>{item.quantity}</span>
              <button onClick={() => handleQuantityChange(item.id, 1)}>+</button>
            </div>
            <div className="item-price">${item.price * item.quantity}</div>
          </div>
        ))}
      </div>
      <div className="cart-total">
        Total amount: <span>${totalAmount}</span>
      </div>
        <ActionButtons />
    </div>
  );
};

export default ShoppingCart;
