import React, { useState } from "react";
import HeaderSection from '../Header.js/header';
import TileCards from "../TileCards/TileCards";
import ShowMore from '../ShowMore/showMore';

function Home() {
    const tiles = [
        { id: 1, title: '2023 Tesla Model S', description: 'Experience the ultimate in electric performance...', image: "https://link1" },
        { id: 2, title: '2023 Tesla Model S', description: 'Experience the ultimate in electric performance...', image: "https://link2" },
        { id: 3, title: "Tesla Model S", description: "A fully electric sedan...", image: "https://link3" },
        { id: 1, title: '2023 Tesla Model S', description: 'Experience the ultimate in electric performance...', image: "https://link1" },
        { id: 2, title: '2023 Tesla Model S', description: 'Experience the ultimate in electric performance...', image: "https://link2" },
        { id: 3, title: "Tesla Model S", description: "A fully electric sedan...", image: "https://link3" },
        { id: 1, title: '2023 Tesla Model S', description: 'Experience the ultimate in electric performance...', image: "https://link1" },
        { id: 2, title: '2023 Tesla Model S', description: 'Experience the ultimate in electric performance...', image: "https://link2" },
        { id: 3, title: "Tesla Model S", description: "A fully electric sedan...", image: "https://link3" },
        // More items...
    ];

    const [visibleCount, setVisibleCount] = useState(3);

    const handleShowMore = () => {
        setVisibleCount(prevCount => prevCount + 3);
        console.log(1);
    };

    return (
        <div style={{ padding: '20px', maxWidth: '1500px', marginLeft: '200px' }}>
            <HeaderSection />
            <TileCards tiles={tiles.slice(0, visibleCount)} />
            <ShowMore onClick={handleShowMore} />
        </div>
    );
}

export default Home;
