import React, { useState, useEffect } from 'react';
import Spinner from './spinner';
import axios from 'axios';

const CarDataProvider = ({ children }) => {
    const [cars, setCars] = useState([]); 
    const [loading, setLoading] = useState(true); 

    useEffect(() => {
        const fetchCarData = async () => {
            try {
                const response = await axios.get('http://localhost:5001/api/cars'); // Змінити URL на ваш API
                setCars(response.data); // Зберігаємо дані в state
            } catch (error) {
                console.error("Error fetching car data:", error);
            } finally {
                setLoading(false); // При будь-якому результаті припиняємо завантаження
            }
        };

        fetchCarData(); 
    }, []); 


    if (loading) {
        return <Spinner />;
    }

    return children(cars);
};

export default CarDataProvider;
